package practiceprogram;

public class ASCI {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char akki='b';
		int  ascii= akki;
		// u can also cast char to int
		// cast means converting
		
		int castAscii= (int) akki;
		
		System.out.println("The ASCII value of " + akki + " is " +ascii);
		System.out.println("The ASCII value of " + akki + " is " +castAscii);
		
		//create char variables
		char a= '5';
		char b='e';
		//convert char variables to int 
		//ASCII value of characters is assigned
		int num1=a;
		int numb=b;
		//print the values
		System.out.println(num1);
		System.out.println(numb);
		
		
		//new
		
		char B='S';
		char D='#';
		int akkid=B;
		int akkis=D;
		System.out.println(akkid);
		System.out.println(akkis);
		
		
		
		
		

	}

}
